DROP FUNCTION IF EXISTS local_concept_mapped_to_ciel;
DELIMITER //
CREATE FUNCTION local_concept_mapped_to_ciel(CIEL_CODE VARCHAR(255)) RETURNS INTEGER
BEGIN
  DECLARE LOCAL_CODE INTEGER DEFAULT NULL;

  SELECT concept_id INTO LOCAL_CODE
  FROM concept_reference_map
  WHERE concept_map_type_id = 
      (
        SELECT concept_map_type_id
        FROM concept_map_type
        WHERE name = 'SAME-AS'
          AND not retired
      )
    AND concept_reference_term_id =
      (
        SELECT concept_reference_term_id
        FROM concept_reference_term
        WHERE concept_source_id =
          (
            SELECT concept_source_id
            FROM concept_reference_source
            WHERE name = 'CIEL'
              AND not retired
          )
          AND code = CIEL_CODE
      );

  IF LOCAL_CODE IS NULl THEN
    SET @ERR_MSG = concat('Failed to find local concept mapped to CIEL:',CIEL_CODE);
    SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = @ERR_MSG;
  END IF;

  RETURN LOCAL_CODE;
END; //
DELIMITER ;

SET @LAST_RUN = :lastExecutionTime;

// CIEL:5356 = CURRENT WHO HIV STAGE (5356)
SET @CURRENT_WHO_HIV_STAGE = local_concept_mapped_to_ciel('5356');

// CIEL:1206 = WHO STAGE 3 ADULT
SET @WHO_STAGE_3_ADULT = local_concept_mapped_to_ciel('1206');

// CIEL:1207 = WHO STAGE 4 ADULT
SET @WHO_STAGE_4_ADULT = local_concept_mapped_to_ciel('1207');

// CIEL:1222 = WHO STAGE 3 PEDS
SET @WHO_STAGE_3_PEDS = local_concept_mapped_to_ciel('1222');

// CIEL:1223 = WHO STAGE 4 PEDS
SET @WHO_STAGE_4_PEDS = local_concept_mapped_to_ciel('1223');

SELECT person_id
FROM obs
WHERE
  UNIX_TIMESTAMP(obs_datetime) > @LAST_RUN
  AND
  (
    concept_id = @CURRENT_WHO_HIV_STAGE
    AND value_coded in
      (
        @WHO_STAGE_3_ADULT,
        @WHO_STAGE_4_ADULT,
        @WHO_STAGE_3_PEDS,
        @WHO_STAGE_4_PEDS
      )
  AND not voided;
